</template>

<script>
export default {
  head() {
    return {
      script: [
        {
          hid: 'animation1',
          async: true,
          innerHTML: `
      (function() {
        var namespace = '__twreporterEmbeddedData';
        var pkg = 'scrollable-video';
        if (typeof window != 'undefined') {
          if (!window.hasOwnProperty(namespace)) {
